import React, { useState, useEffect } from 'react';
import { Helmet } from 'react-helmet';
import Header from '../components/header';
import './voting.css';

const Voting = (props) => {
  const [criteria, setCriteria] = useState([]);
  const [selectedCriterion, setSelectedCriterion] = useState(null);
  const [participants, setParticipants] = useState([]);
  const [selectedParticipant, setSelectedParticipant] = useState(null);
  const [isVotingCompleted, setIsVotingCompleted] = useState(false);

  useEffect(() => {
    // Fetch criteria and participants from the backend
    fetch('/api/voting/criteria')
      .then(response => response.json())
      .then(data => setCriteria(data))
      .catch(error => console.error('Error fetching criteria:', error));

    fetch(`/api/voting/participants?gameId=${props.gameId}`)
      .then(response => response.json())
      .then(data => setParticipants(data))
      .catch(error => console.error('Error fetching participants:', error));
  }, [props.gameId]);

  const handleCriterionChange = (event) => {
    setSelectedCriterion(event.target.value);
  };

  const handleParticipantChange = (event) => {
    setSelectedParticipant(event.target.value);
  };

  const handleVote = () => {
    if (selectedCriterion && selectedParticipant) {
      fetch('/api/voting/vote', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          gameId: props.gameId,
          voterId: props.userId,
          participantId: selectedParticipant,
          criterionId: selectedCriterion,
        }),
      })
        .then(response => response.json())
        .then(data => {
          console.log('Vote submitted:', data);
          setIsVotingCompleted(true);
        })
        .catch(error => console.error('Error submitting vote:', error));
    } else {
      alert('Please select a criterion and a participant');
    }
  };

  return (
    <div className="voting-container">
      <Helmet>
        <title>Voting - Outstanding Old Bison</title>
        <meta property="og:title" content="Voting - Outstanding Old Bison" />
      </Helmet>
      <Header rootClassName="header-root-class-name"></Header>
      <h1 className="voting-text">Голосование</h1>
      {isVotingCompleted ? (
        <div>Спасибо за ваше голосование!</div>
      ) : (
        <div>
          <div className="voting-dropdown">
            <label htmlFor="criterion">Выберите критерий:</label>
            <select id="criterion" onChange={handleCriterionChange}>
              <option value="">--Выберите критерий--</option>
              {criteria.map((criterion) => (
                <option key={criterion.id} value={criterion.id}>
                  {criterion.name}
                </option>
              ))}
            </select>
          </div>
          <div className="voting-dropdown">
            <label htmlFor="participant">Выберите участника:</label>
            <select id="participant" onChange={handleParticipantChange}>
              <option value="">--Выберите участника--</option>
              {participants.map((participant) => (
                <option key={participant.id} value={participant.id}>
                  {participant.username}
                </option>
              ))}
            </select>
          </div>
          <button onClick={handleVote}>Проголосовать</button>
        </div>
      )}
    </div>
  );
};

export default Voting;
