import React, { useState, useEffect } from 'react';

const Voting = ({ gameId, voterId }) => {
    const [criteria, setCriteria] = useState([]);
    const [participants, setParticipants] = useState([]);
    const [votes, setVotes] = useState({});
    const [error, setError] = useState(null);

    useEffect(() => {
        fetch('/api/voting/criteria')
            .then(response => {
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                return response.json();
            })
            .then(data => setCriteria(data))
            .catch(error => {
                console.error('Error fetching criteria:', error);
                setError('Error fetching criteria');
            });

        fetch(`/api/voting/participants?gameId=${gameId}`)
            .then(response => {
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                return response.json();
            })
            .then(data => setParticipants(data))
            .catch(error => {
                console.error('Error fetching participants:', error);
                setError('Error fetching participants');
            });
    }, [gameId]);

    const handleVoteChange = (criterionId, participantId) => {
        setVotes(prevVotes => ({
            ...prevVotes,
            [criterionId]: participantId
        }));
    };

    const handleSubmit = () => {
        const voteData = Object.keys(votes).map(criterionId => ({
            gameId,
            voterId,
            participantId: votes[criterionId],
            criterionId: parseInt(criterionId, 10)
        }));

        fetch('/api/voting/vote', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(voteData)
        })
        .then(response => {
            if (response.ok) {
                alert('Спасибо за ваше голосование!');
            } else {
                alert('Ошибка при отправке голосов.');
            }
        })
        .catch(error => console.error('Error submitting votes:', error));
    };

    if (error) {
        return <div>Error: {error}</div>;
    }

    return (
        <div className="voting-container">
            <h1>Голосование</h1>
            {criteria.map(criterion => (
                <div key={criterion.id}>
                    <label>{criterion.name}</label>
                    <select onChange={e => handleVoteChange(criterion.id, e.target.value)}>
                        <option value="">Выберите участника</option>
                        {participants.map(participant => (
                            <option key={participant.id} value={participant.id}>
                                {participant.username}
                            </option>
                        ))}
                    </select>
                </div>
            ))}
            <button onClick={handleSubmit}>Проголосовать</button>
        </div>
    );
};

export default Voting;
