import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Bar } from 'react-chartjs-2';
import { useHistory } from 'react-router-dom';
import { Helmet } from 'react-helmet';
import Header from '../components/header';
import './profile.css';

const Profile = () => {
    const [profile, setProfile] = useState(null);
    const [error, setError] = useState(null);
    const [votingStatuses, setVotingStatuses] = useState({});
    const history = useHistory();

    useEffect(() => {
        const fetchProfile = async () => {
            try {
                const response = await axios.get('http://localhost:8080/api/profile');
                setProfile(response.data);
            } catch (err) {
                setError('Failed to fetch profile');
                console.error('Error fetching profile:', err);
            }
        };

        fetchProfile();
    }, []);

    useEffect(() => {
        const fetchVotingStatuses = async () => {
            if (profile && profile.games) {
                const statuses = {};
                for (const game of profile.games) {
                    try {
                        const response = await axios.get(`http://localhost:8080/api/voting/status?gameId=${game.id}`);
                        statuses[game.id] = response.data.votingClosed;
                    } catch (err) {
                        setError(`Failed to fetch voting status for game ${game.id}`);
                        console.error('Error fetching voting status:', err);
                    }
                }
                setVotingStatuses(statuses);
            }
        };

        fetchVotingStatuses();
    }, [profile]);

    const chartData = {
        labels: ['Критерий 1', 'Критерий 2', 'Критерий 3'],
        datasets: [
            {
                label: 'Очки',
                data: [12, 19, 3],
                backgroundColor: 'rgba(75, 192, 192, 0.2)',
                borderColor: 'rgba(75, 192, 192, 1)',
                borderWidth: 1
            }
        ]
    };

    const chartOptions = {
        scales: {
            y: {
                beginAtZero: true,
                ticks: {
                    color: 'white',
                },
                grid: {
                    color: 'rgba(255, 255, 255, 0.2)',
                }
            },
            x: {
                ticks: {
                    color: 'white',
                },
                grid: {
                    color: 'rgba(255, 255, 255, 0.2)',
                }
            }
        },
        plugins: {
            legend: {
                labels: {
                    color: 'white',
                }
            }
        },
        maintainAspectRatio: false,
    };

    const closeVoting = async (gameId) => {
        try {
            await axios.post('http://localhost:8080/api/voting/close', null, {
                params: { gameId }
            });
            setVotingStatuses({
                ...votingStatuses,
                [gameId]: true
            });
        } catch (err) {
            setError(`Failed to close voting: ${err.message}`);
            console.error('Error closing voting:', err);
        }
    };

    if (error) {
        return <div className="error">{error}</div>;
    }

    if (!profile) {
        return <div>Loading...</div>;
    }

    return (
        <div className="profile-container">
            <Helmet>
                <title>Profile - Outstanding Old Bison</title>
                <meta property="og:title" content="Profile - Outstanding Old Bison" />
            </Helmet>
            <Header rootClassName="header-root-class-name2"></Header>
            <div className="profile-container1">
                <h1 className="profile-text">
                    <span>Мой профиль</span>
                    <br />
                </h1>
                <div className="profile-profile-info">
                    <div className="profile-username">
                        <span className="profile-text03">
                            <span>Имя пользователя: </span>
                            <br />
                        </span>
                        <span className="profile-text06">
                            <span>{profile.user.username}</span>
                            <br />
                        </span>
                    </div>
                    <div className="profile-count-game">
                        <span className="profile-text09">
                            <span>Количество игр:</span>
                            <br />
                        </span>
                        <span className="profile-text12">
                            <span>{profile.gameCount}</span>
                            <br />
                        </span>
                    </div>
                    <div className="profile-best-roles">
                        <span className="profile-text15">
                            <span>Лучшие роли:</span>
                            <br />
                        </span>
                        <span className="profile-text18">
                            <span>1</span>
                            <br />
                        </span>
                    </div>
                </div>
                <div className="chart-container" style={{ backgroundColor: '#0E91EF', padding: '20px', borderRadius: '10px', marginBottom: '20px' }}>
                    <h2 style={{ color: 'white' }}>Результаты голосования</h2>
                    <div style={{ height: '400px', width: '100%' }}>
                        <Bar data={chartData} options={chartOptions} />
                    </div>
                </div>
                {profile.games.map((game) => (
                    <div key={game.id} className="profile-game">
                        <span className="profile-text21">{game.gameName}</span>
                        <span className="profile-text22">{new Date(game.date).toLocaleDateString()}</span>
                        <button
                            type="button"
                            className="button profile-button"
                            onClick={() => history.push(`/voting/${game.id}`)}
                        >
                            <span>
                                <span>Голосование</span>
                                <br />
                            </span>
                        </button>
                        {!votingStatuses[game.id] && (
                            <button
                                type="button"
                                className="button profile-button"
                                onClick={() => closeVoting(game.id)}
                            >
                                <span>
                                    <span>Закрыть голосование</span>
                                    <br />
                                </span>
                            </button>
                        )}
                        {votingStatuses[game.id] && (
                            <span style={{ color: 'red' }}>Голосование закрыто</span>
                        )}
                    </div>
                ))}
            </div>
        </div>
    );
};

export default Profile;
