import React, { useState } from 'react';
import axios from 'axios';
import { Helmet } from 'react-helmet';
// import './create-game.css';

const CreateGame = (props) => {
  const [gameName, setGameName] = useState('');
  const [numberOfPlayers, setNumberOfPlayers] = useState(0);
  const [currentPlayers, setCurrentPlayers] = useState(0);
  const [description, setDescription] = useState('');
  const [date, setDate] = useState('');
  const [time, setTime] = useState('');
  const [error, setError] = useState('');

  const handleGameNameChange = (e) => {
    setGameName(e.target.value);
  };

  const handleNumberOfPlayersChange = (e) => {
    setNumberOfPlayers(e.target.value);
  };

  const handleCurrentPlayersChange = (e) => {
    setCurrentPlayers(e.target.value);
  };

  const handleDescriptionChange = (e) => {
    setDescription(e.target.value);
  };

  const handleDateChange = (e) => {
    setDate(e.target.value);
  };

  const handleTimeChange = (e) => {
    setTime(e.target.value);
  };

  const handleSubmit = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.post('http://localhost:8080/api/games', {
        gameName,
        numberOfPlayers,
        currentPlayers,
        description,
        date,
        time,
      }, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      if (response.status === 201) {
        console.log('Game created successfully');
        // Perform actions upon successful game creation
      } else {
        setError('Error creating game');
      }
    } catch (error) {
      setError('An error occurred during game creation');
      console.error('Create game error', error);
    }
  };

  return (
    <div className="create-game-container">
      <Helmet>
        <title>Create Game</title>
        <meta property="og:title" content="Create Game" />
      </Helmet>
      <h1 className="create-game-text">Create a New Game</h1>
      <div className="create-game-inputs">
        <input
          type="text"
          placeholder="Game Name"
          value={gameName}
          onChange={handleGameNameChange}
        />
        <input
          type="number"
          placeholder="Number of Players"
          value={numberOfPlayers}
          onChange={handleNumberOfPlayersChange}
        />
        <input
          type="number"
          placeholder="Current Players"
          value={currentPlayers}
          onChange={handleCurrentPlayersChange}
        />
        <textarea
          placeholder="Description"
          value={description}
          onChange={handleDescriptionChange}
        ></textarea>
        <input
          type="date"
          value={date}
          onChange={handleDateChange}
        />
        <input
          type="time"
          value={time}
          onChange={handleTimeChange}
        />
      </div>
      {error && <div className="create-game-error">{error}</div>}
      <button onClick={handleSubmit}>Create Game</button>
    </div>
  );
};

export default CreateGame;
