import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Helmet } from 'react-helmet';
import './anounce.css';

const Anounce = () => {
  const [games, setGames] = useState([]);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchGames = async () => {
      try {
        const response = await axios.get('http://localhost:8080/api/games');
        setGames(response.data);
      } catch (err) {
        setError('Failed to fetch games');
        console.error(err);
      }
    };

    fetchGames();
  }, []);

  const handleJoinGame = async (gameId) => {
    try {
      const response = await axios.post('http://localhost:8080/api/join-game', { gameId });
      if (response.status === 200) {
        const updatedGames = games.map((game) =>
          game.id === gameId ? { ...game, currentPlayers: game.currentPlayers + 1 } : game
        );
        setGames(updatedGames);
      }
    } catch (err) {
      setError('Failed to join game');
      console.error(err);
    }
  };

  return (
    <div className="anounce-container">
      <Helmet>
        <title>Anounce - Voting game</title>
        <meta property="og:title" content="Anounce - Voting game" />
      </Helmet>
      <header className="header-root-class-name1"></header>
      <h1 className="anounce-text">Анонсы</h1>
      {error && <p className="anounce-error">{error}</p>}
      <div className="anounce-game-list">
        {games.map((game) => (
          <div key={game.id} className="anounce-game-card">
            <span className="anounce-text03">Игра</span>
            <span className="anounce-text04">{game.gameName}</span>
            <div className="anounce-info">
              <div className="anounce-container1">
                <span className="anounce-text05">Дата</span>
                <span className="anounce-text06">{game.date}</span>
              </div>
              <div className="anounce-container2">
                <span className="anounce-text07">Время</span>
                <span className="anounce-text08">{game.time}</span>
              </div>
              <div className="anounce-container3">
                <span className="anounce-text11">Число игроков</span>
                <span className="anounce-text14">
                  {game.currentPlayers}/{game.numberOfPlayers}
                </span>
              </div>
            </div>
            <div className="anounce-description">
              <span className="anounce-text17">Описание</span>
              <span className="anounce-text18">{game.description}</span>
            </div>
            <button
              type="button"
              className="button anounce-button"
              onClick={() => handleJoinGame(game.id)}
            >
              Записаться
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Anounce;
