import React, { useState, useEffect } from 'react'
import { Helmet } from 'react-helmet'
import Header from '../components/header'
import './voting.css'

const Voting = (props) => {
  const [players, setPlayers] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchPlayers = async () => {
      try {
        const response = await fetch('/api/players?gameId=1'); // используйте реальный gameId
        if (!response.ok) {
          throw new Error(`Error fetching players: ${response.statusText}`);
        }
        const data = await response.json();
        setPlayers(data);
      } catch (error) {
        setError(error.message);
      } finally {
        setIsLoading(false);
      }
    };

    fetchPlayers();
  }, []);

  if (isLoading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>{error}</div>;
  }

  return (
    <div className="voting-container">
      <Helmet>
        <title>Voting - Outstanding Old Bison</title>
        <meta property="og:title" content="Voting - Outstanding Old Bison" />
      </Helmet>
      <Header rootClassName="header-root-class-name"></Header>
      <h1 className="voting-text">Голосование</h1>

      {["Лидер общественного мнения", "Автор ключевых идей", "Достоин стать президентом", "Топ 3 коммуникативных навыков", "Топ 3 положительных героев"].map((criterion, index) => (
        <div key={index} data-thq="thq-dropdown" className={`voting-thq-dropdown${index} list-item`}>
          <div data-thq="thq-dropdown-toggle" className={`voting-dropdown-toggle${index}`}>
            <svg viewBox="0 0 1024 1024" className={`voting-icon${index}`}>
              <path d="M426 726v-428l214 214z"></path>
            </svg>
            <span>{criterion}</span>
            <div data-thq="thq-dropdown-arrow" className={`voting-dropdown-arrow${index}`}></div>
          </div>
          <ul data-thq="thq-dropdown-list" className={`voting-dropdown-list${index}`}>
            {players.map(player => (
              <li key={player.id} data-thq="thq-dropdown" className={`voting-dropdown${index} list-item`}>
                <div data-thq="thq-dropdown-toggle" className={`voting-dropdown-toggle${index}`}>
                  <span className={`voting-text${index}`}>{player.username}</span>
                </div>
              </li>
            ))}
          </ul>
        </div>
      ))}

      <button onClick={submitVote} className="voting-submit-button">Submit Vote</button>
    </div>
  );

  async function submitVote() {
    const voteData = {
      userId: 1, // замените на реальный userId
      gameId: 1, // замените на реальный gameId
      opinionLeader: 1, // замените на выбранный userId
      ideaAuthor: 2, // замените на выбранный userId
      futurePresident: 3, // замените на выбранный userId
      topCommunicators: [4, 5, 6], // замените на выбранные userId
      topHeroes: [7, 8, 9], // замените на выбранные userId
    };

    try {
      const response = await fetch('/api/vote', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(voteData),
      });

      if (!response.ok) {
        throw new Error(`Error submitting vote: ${response.statusText}`);
      }

      alert('Vote submitted successfully!');
    } catch (error) {
      alert(error.message);
    }
  }
}

export default Voting;
