import React, { useState, useEffect } from 'react';
import { Helmet } from 'react-helmet';
import Header from '../components/header';
import './voting.css';

const Voting = ({ userId, gameId }) => {
  const [players, setPlayers] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);
  const [selections, setSelections] = useState({
    opinionLeader: null,
    ideaAuthor: null,
    futurePresident: null,
    topCommunicators: [null, null, null],
    topHeroes: [null, null, null],
  });

  useEffect(() => {
    const fetchPlayers = async () => {
      try {
        const response = await fetch(`/api/players?gameId=${gameId}`);
        if (!response.ok) {
          throw new Error(`Error fetching players: ${response.statusText}`);
        }
        const data = await response.json();
        setPlayers(data);
      } catch (error) {
        setError(error.message);
      } finally {
        setIsLoading(false);
      }
    };

    fetchPlayers();
  }, [gameId]);

  const handleSelectionChange = (category, value, index = null) => {
    if (index !== null) {
      const updatedArray = [...selections[category]];
      updatedArray[index] = value;
      setSelections({
        ...selections,
        [category]: updatedArray,
      });
    } else {
      setSelections({
        ...selections,
        [category]: value,
      });
    }
  };

  async function submitVote() {
    const voteData = {
      userId, 
      gameId, 
      opinionLeader: selections.opinionLeader,
      ideaAuthor: selections.ideaAuthor,
      futurePresident: selections.futurePresident,
      topCommunicators: selections.topCommunicators.filter((id) => id !== null),
      topHeroes: selections.topHeroes.filter((id) => id !== null),
    };

    try {
      const response = await fetch('/api/vote', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(voteData),
      });

      if (!response.ok) {
        throw new Error(`Error submitting vote: ${response.statusText}`);
      }

      alert('Vote submitted successfully!');
    } catch (error) {
      alert(error.message);
    }
  }

  if (isLoading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>{error}</div>;
  }

  const renderDropdown = (category, label, multiple = false, count = 1) => {
    return (
      <div data-thq="thq-dropdown" className="voting-thq-dropdown list-item">
        <div data-thq="thq-dropdown-toggle" className="voting-dropdown-toggle">
          <svg viewBox="0 0 1024 1024" className="voting-icon">
            <path d="M426 726v-428l214 214z"></path>
          </svg>
          <span>{label}</span>
          <div data-thq="thq-dropdown-arrow" className="voting-dropdown-arrow"></div>
        </div>
        <ul data-thq="thq-dropdown-list" className="voting-dropdown-list">
          {multiple
            ? [...Array(count).keys()].map((_, index) => (
                <li key={index} data-thq="thq-dropdown" className="voting-dropdown list-item">
                  <select
                    onChange={(e) => handleSelectionChange(category, e.target.value, index)}
                    value={selections[category][index] || ''}
                  >
                    <option value="" disabled>Select player</option>
                    {players.map((player) => (
                      <option key={player.id} value={player.id}>
                        {player.username}
                      </option>
                    ))}
                  </select>
                </li>
              ))
            : players.map((player) => (
                <li key={player.id} data-thq="thq-dropdown" className="voting-dropdown list-item">
                  <select
                    onChange={(e) => handleSelectionChange(category, e.target.value)}
                    value={selections[category] || ''}
                  >
                    <option value="" disabled>Select player</option>
                    {players.map((player) => (
                      <option key={player.id} value={player.id}>
                        {player.username}
                      </option>
                    ))}
                  </select>
                </li>
              ))}
        </ul>
      </div>
    );
  };

  return (
    <div className="voting-container">
      <Helmet>
        <title>Voting - Outstanding Old Bison</title>
        <meta property="og:title" content="Voting - Outstanding Old Bison" />
      </Helmet>
      <Header rootClassName="header-root-class-name"></Header>
      <h1 className="voting-text">Голосование</h1>
      {renderDropdown('opinionLeader', 'Лидер общественного мнения')}
      {renderDropdown('ideaAuthor', 'Автор ключевых идей')}
      {renderDropdown('futurePresident', 'Достоин стать президентом')}
      {renderDropdown('topCommunicators', 'Топ 3 коммуникативных навыков', true, 3)}
      {renderDropdown('topHeroes', 'Топ 3 положительных героев', true, 3)}
      <button onClick={submitVote} className="voting-submit-button">Submit Vote</button>
    </div>
  );
};

export default Voting;
